function PLOT_carpPlay(latencies, cs, clist)% function PLOT_carpPlay(latencies, cs, clist)%%tmin = 150;tinf = 2000;co = {'k' 'c' 'm' 'r' 'g' 'b'};fig = figure;wysifig(fig);fig2 = figure;wysifig(fig2);for i = 1:length(clist)    % Carp graph  figure(fig);  t = latencies(cs == clist(i));  [muhat,shat,muCI,sCI,b_new,tz,hPoints,hPred,hl] = fitBetterCarpG(t, tmin, tinf);  set(hPoints, 'Color', co{i});  set(hPred, 'Color', co{i});    % test norm fit  figure(fig2);  subplot(round(length(clist)/2),2,i);  jigax(gca);  [nn yy] = hist(1./t, 20);  [muhat,shat,muCI,sCI] = normfit(1./t,0.05);  pdf = normpdf(yy,muhat,shat);  hold on  bar(yy, nn/sum(nn), 1, co{i});  plot(yy,pdf/sum(pdf), co{i});    title(sprintf('n=%d, m=%.3g, s=%.3g\ncoh=%d', length(t), muhat, shat, clist(i)));%  input(sprintf('mean = %d', muhat));  end